mapper.select:
SELECT userName, macAddress, GROUP_CONCAT( upTime SEPARATOR ';' ) AS upTimeList, GROUP_CONCAT( savingTime SEPARATOR ';' ) AS savingTimeList, GROUP_CONCAT( watt SEPARATOR ';' ) AS wattList, regDate from( SELECT L.USER_NAME AS userName , L.MACADDRESS AS macAddress , DATE_FORMAT(L.REG_DATE,'%Y-%m-%d') AS regDate , max(L.uptime) AS upTime , max(L.savingtime) AS savingTime , E.WATT as watt FROM TBL_LOG_INFO L, TBL_EQUIPMENT_INFO E
L.MACADDRESS = E.MACADDRESS AND DATE_FORMAT(L.REG_DATE,'%Y%m%d') >= #{param.beforeday} and DATE_FORMAT(L.REG_DATE,'%Y%m%d') <= #{param.afterday}
AND L.USER_NAME = #{param.searchValue}
AND L.MACADDRESS = #{param.searchValue}
GROUP BY regDate, E.MACADDRESS ) day GROUP BY regDate

SELECT userName, macAddress, GROUP_CONCAT( dupTimeList SEPARATOR ';' ) AS upTimeList, GROUP_CONCAT( dsavingTimeList SEPARATOR ';' ) AS savingTimeList, GROUP_CONCAT( dwattList SEPARATOR ';' ) AS wattList, DATE_FORMAT(dregDate,'%Y-%m') AS regDate from( SELECT userName, macAddress, GROUP_CONCAT( upTime SEPARATOR ';' ) AS dupTimeList, GROUP_CONCAT( savingTime SEPARATOR ';' ) AS dsavingTimeList, GROUP_CONCAT( watt SEPARATOR ';' ) AS dwattList, dregDate from( SELECT L.USER_NAME AS userName , L.MACADDRESS AS macAddress , DATE_FORMAT(L.REG_DATE,'%Y-%m-%d') AS dregDate , max(L.uptime) AS upTime , max(L.savingtime) AS savingTime , E.WATT as watt FROM TBL_LOG_INFO L, TBL_EQUIPMENT_INFO E
L.MACADDRESS = E.MACADDRESS AND DATE_FORMAT(L.REG_DATE,'%Y%m') >= DATE_FORMAT(STR_TO_DATE(#{param.monthbeforeday},'%Y%m')+1,'%Y%m') AND DATE_FORMAT(L.REG_DATE,'%Y%m') < DATE_FORMAT(DATE_ADD(STR_TO_DATE(#{param.monthafterday},'%Y%m')+1,interval 1 MONTH),'%Y%m')
AND L.USER_NAME = #{param.searchValue}
AND L.MACADDRESS = #{param.searchValue}
GROUP BY dregDate, E.MACADDRESS ) day GROUP BY dregDate ) month GROUP BY regDate
