mapper.select:
SELECT USER_SEQ AS userSeq , EQUIP_SEQ AS equipSeq , VALUE AS value , SUM(SUM_POWER) AS sumPower , DATE_FORMAT(REG_DATE,'%Y-%m-%d') AS regDate FROM TBL_LOG_INFO
REG_DATE >= STR_TO_DATE(#{param.beforeday},'%Y%m%d') and REG_DATE <= STR_TO_DATE(#{param.afterday},'%Y%m%d')
AND USER_SEQ = #{param.searchValue}
AND EQUIP_SEQ = #{param.searchValue}
GROUP BY regDate ORDER BY REG_DATE DESC
