mapper.select:
SELECT equip_seq, name, manufacturer, make_date, etc, elect_power, reg_date, mod_date FROM TBL_EQUIPMENT_INFO
name LIKE CONCAT('%', #{param.searchValue}, '%')
ORDER BY reg_date DESC LIMIT #{param.startRow}, #{param.rowPerPage}

SELECT equip_seq, name, manufacturer, make_date, etc, elect_power, reg_date, mod_date
FROM TBL_EQUIPMENT_INFO

	equip_seq = #{equip_seq}

SELECT ADMIN_ID AS id , ADMIN_GROUP_SEQ AS groupId , PASSWD AS passwd , NAME AS name , MOBILE AS mobile , EMAIL AS email , REG_DATE AS fstRgDt , MOD_DATE AS lstChDt FROM TBL_ADMIN
1 = 1
AND ADMIN_ID = #{userId}

SELECT ADMIN_GROUP_SEQ AS id , NAME AS name , DESCRIPTION AS description , REG_DATE AS fstRgDt , MOD_DATE AS lstChDt FROM TBL_ADMIN_GROUP
1 = 1
AND ADMIN_GROUP_SEQ = #{groupId}

SELECT ADMIN_GROUP_SEQ AS adminGroupId , MENU_SEQ AS menuId , AUTH AS auth , REG_DATE AS fstRgDt , MOD_DATE AS lstChDt FROM TBL_ADMIN_GROUPAUTH
1 = 1
AND ADMIN_GROUP_SEQ = #{groupId}

SELECT SN_SEQ AS id , ADMIN_ID AS adminId , SESSION_ID AS sessionId , HOST AS host , PORT AS port , REG_DATE AS fstRgDt , MOD_DATE AS fstMdDt FROM TBL_ADMIN_SESSION
1 = 1
AND ADMIN_ID = #{param.adminId}
AND SESSION_ID = #{param.sessionId}

mapper.update:
update TBL_ADMIN set
  NAME = #{param.name},
  MOBILE = #{param.mobile},
  EMAIL = #{param.email},
  MOD_DATE = #{param.LstChDt}
where ADMIN_ID = #{param.id}

update TBL_ADMIN set
  PASSWD = #{param.passwd},
  MOD_DATE = #{param.modDate}
where ADMIN_ID = #{param.id}
