mapper.insert:
INSERT INTO TBL_NOTICE
	(
	ADMIN_ID, REG_DATE
	, TITLE
	, CONTENTS
	, DISPLAY_YN
	)
VALUES
	(
	#{param.adminId}, #{param.regDate}
	, #{param.title}
	, #{param.contents}
	, #{param.displayYn}
	)

mapper.update:
UPDATE
	TBL_NOTICE
SET
	ADMIN_ID = #{param.adminId}
	, MOD_DATE = #{param.modDate}
	, DISPLAY_YN = #{param.displayYn} 
	, TITLE = #{param.title}
	, CONTENTS = #{param.contents}
WHERE
	SEQ = #{param.id}
